mapper.select:
select *
  from diagnosis_history
 where history_id = #{historyId}
   and file_id = #{fileId}

select *
from diagnosis_result
where history_id = #{historyId}
  and file_id = #{fileId}

select *
from diagnosis_result
where history_id = #{historyId}
  and file_id = #{fileId}

mapper.update:
update file_history
   set status = #{status},
       error_yn = #{errorYn},
       fixed_file = #{fixedFile}
 where history_id = #{historyId}
   and file_id = #{fileId}
